// --- Design Constants (New Theme) ---
export const COLORS = {
    background: '#0D0D0D', // True black base
    primaryAccent: '#00FFAA', // Neon emerald green (Sberbank)
    secondaryAccent: '#1A1A1A', // Charcoal (for card/backgrounds)
    tertiary: '#2E2E2E', // Light black/gray for contrast
    typography: '#EAEAEA', // Soft white
    buttonsGlow: 'rgba(0, 255, 170, 0.5)', // Semi-transparent green glow
};

// Region codes for KYC
export const REGION_CODES = {
    'Volga Valley': 'VLV',
    'Siberian Frontier': 'SIB',
    'Northern Frontier': 'NFR',
    'Central Plains': 'CPL',
    'Southern Coast': 'SCO',
};
